package LoopsExc;

public class MultiplicationLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 24;
		int num2 = 50;
		int num3 = 29;
		System.out.println("24th table");
		for(int i=1; i<=10; i++) {
			System.out.println(num1*i);
		}
		System.out.println("50th table");
		for(int i=1; i<=10; i++) {
			System.out.println(num2*i);
		}
		System.out.println("29th table");
		for(int i=1; i<=10; i++) {
			System.out.println(num3*i);
		}	
	}

}
